import React, { Component } from 'react';
import { ProgressBar, Dropdown, Button,Modal,FormGroup } from 'react-bootstrap';
import axios from 'axios';
import "./shop.scss";

// import DatePicker from 'react-datepicker';
// import { Dropdown } from 'react-bootstrap';

export class ShopCategory extends Component {
  constructor(props) {
    super(props);
    this.state = {
      startDate : new Date(),
      persons : [],
      categories : [],
      show: false ,
      categoryName : '',
      categoryDescription :'',
      nameError : '',
      fields : [],

    }
    this.inputNameChangeHandler = this.inputNameChangeHandler.bind(this);
    this.inputDescriptionChangeHandler = this.inputDescriptionChangeHandler.bind(this);
    this.onClickButtonAdder = this.onClickButtonAdder.bind(this);
    // this.onClickButtonSubmit = this.onClickButtonSubmit.bind(this);
  } 
  componentDidMount() {
    this.fetchUsers();
    this.fetchCategories();
  }

fetchUsers (){
  axios.get(`https://jsonplaceholder.typicode.com/users`)
      .then(res => {
        const personss = res.data;
        this.setState({ persons:personss });
      })
}
fetchCategories (){
    axios.get('http://localhost:5000/ShopCategories')
        .then(res => {
          const categories = res.data;
          this.setState({ categories:categories.categories });
        })
}

postCategories (){

  var categories = {}
  axios.post('http://localhost:5000/ShopCategories',)
      .then(res => {
        this.fetchCategories();
      })
}
fetchLocation (){
  var options = {
    enableHighAccuracy: true,
    timeout: 5000,
    maximumAge: 0
  };
  var e = '';
  navigator.geolocation.getCurrentPosition(function(pos){
    var crd = pos.coords;
    console.log(pos)
    var position = [crd.latitude,crd.longitude];
    console.log('position',position)
    axios.post('http://localhost:5000/updateLocation',{position:position})
    .then(res => {
      console.log(res.data);
    })
  }, function(e){
    console.log(e);

  }, options);
}

inputNameChangeHandler(event) {
    this.setState({categoryName : event.target.value});
    if(this.state.categoryName.length < 3){

      this.setState({nameError : 'filed is too short'})
    } else {
      this.setState({nameError : ''})
    }

    console.log(this.state.categoryName)
}
inputDescriptionChangeHandler(event) {
  this.setState({categoryDescription : event.target.value});
}


toggleProBanner() {
  document.querySelector('.proBanner').classList.toggle("hide");
}
showModal = () => {
  this.setState({ show: true });
}

hideModal = () => {
  this.setState({ show: false });
}

onClickFormGroupButton(index) {
  let fields = [...this.state.fields];
  fields.splice(index, 1);
  this.setState({ fields });
}

onChangeFormGroupInput(index, event) {
  let fields = [...this.state.fields];
  fields[index] = event.target.value;
  this.setState({ fields });
}
onClickButtonAdder(event) {
  event.preventDefault();
  this.setState({
    fields: ["", ...this.state.fields]
  });
}
  render () {
    const isFormGroupDeletionAllowed =
    this.state.fields.length > 1 ? true : false;
    return (
      <div>
        <div className="row  banner" >
          <div className="col-10">

              <p>Shop categories</p>

          </div>
          <div className="col-2" ><span className="d-flex justify-content-end" styles="{{align-self:'flex-end'}}"> <Button  variant="primary" onClick={this.showModal}>
                  Create shop <i className="fa fa-plus"></i>
              </Button></span></div>
        </div>

        <div className="row">
          <div className="col-lg-12 grid-margin">
            <div className="card">
              <div className="card-body">
                <h4 className="card-title">Orders</h4>
                <div className="table-responsive">
                  <table className="table table-striped">
                    <thead>
                      <tr>
                        <th> # </th>
                        <th> Shope name </th>
                        <th> Progress </th>
                        <th> Amount </th>
                        <th> Sales </th>
                        <th> Deadline </th>
                      </tr>
                    </thead>
                    <tbody>
                      {this.state.categories.map((person, index) =>{
                        return (
                      <tr>
                        <td className="font-weight-medium"> {index+1}</td>
                        <td> {person.name} </td>
                        <td>
                          <ProgressBar variant="success" striped now={25}/>
                        </td>
                        <td> $ 77.99 </td>
                        <td className="text-danger"> 53.64% <i className="mdi mdi-arrow-down"></i>
                        </td>
                        <td> May 15, 2015 </td>
                      </tr>)
                      })}

                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
        <Modal show={this.state.show}  onHide={this.hideModal} backdrop="static">
          <Modal.Header closeButton>
            <Modal.Title>Shop categories</Modal.Title>
          </Modal.Header>
          <Modal.Body>
          <form onSubmit={this.handleSubmit} noValidate>
            <div className="form-group">
              <label htmlFor="shopCategory">Name </label>
              <input type="email" className={this.state.nameError != ''?" form-control is-invalid":"form-control"} id="shopCategory" placeholder="Category"  onChange={ this.inputNameChangeHandler}
              autoComplete="none"></input>
              <div className="invalid-feedback" >
              {this.state.nameError}
              </div>
            </div>
            {this.state.fields.map((value, index) => (
              <FormGroup
                inputChange={this.onChangeFormGroupInput.bind(this, index)}
                buttonClick={this.onClickFormGroupButton.bind(this, index)}
                buttonDisabled={index === 0 ? !isFormGroupDeletionAllowed : undefined}
                value={value}
                key={index}
              />
            ))}
            <div className="form-group">
              <label htmlFor="shopCategoryDescription">Example textarea</label>
              <textarea className="form-control" id="shopCategoryDescription" rows="3"  onChange={this.inputDescriptionChangeHandler}></textarea>
            </div>
          </form>

          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.hideModal}>
              Close
            </Button>
            <Button variant="primary">
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </div> 
    );
  }
}


export default ShopCategory;
