import React, { Component } from 'react';
import { ProgressBar, Dropdown } from 'react-bootstrap';
import axios from 'axios';

// import DatePicker from 'react-datepicker';
// import { Dropdown } from 'react-bootstrap';

export class ShopCategory extends Component {
  constructor(props) {
    super(props);
    this.state = {
      startDate : new Date(),
      persons : [],
      categories : []

    }
    this.statusChangedHandler = this.statusChangedHandler.bind(this);
    this.addTodo = this.addTodo.bind(this);
    this.removeTodo = this.removeTodo.bind(this);
    this.inputChangeHandler = this.inputChangeHandler.bind(this);
  } 
  componentDidMount() {
    this.fetchUsers();
    this.fetchCategories();
  }
  statusChangedHandler(event, id) {
    const todo = {...this.state.todos[id]};
    todo.isCompleted = event.target.checked;

    const todos = [...this.state.todos];
    todos[id] = todo;

    this.setState({
        todos: todos
    })
}

fetchUsers (){
  axios.get(`https://jsonplaceholder.typicode.com/users`)
      .then(res => {
        const personss = res.data;
        this.setState({ persons:personss });
      })
}
fetchCategories (){
    axios.get('http://localhost:5000/ShopCategories')
        .then(res => {
          const categories = res.data;
          this.setState({ categories:categories.shops });
        })
  }
fetchLocation (){
  var options = {
    enableHighAccuracy: true,
    timeout: 5000,
    maximumAge: 0
  };
  var e = '';
  navigator.geolocation.getCurrentPosition(function(pos){
    var crd = pos.coords;
    console.log(pos)
    var position = [crd.latitude,crd.longitude];
    console.log('position',position)
    axios.post('http://localhost:5000/updateLocation',{position:position})
    .then(res => {
      console.log(res.data);
    })
  }, function(e){
    console.log(e);

  }, options);
}

addTodo (event) {
    event.preventDefault();

    const todos = [...this.state.todos];
    todos.unshift({
        id: todos.length ? todos[todos.length - 1].id + 1 : 1,
        task: this.state.inputValue,
        isCompleted: false

    })

    this.setState({
        todos: todos,
        inputValue: ''
    })
}

removeTodo (index) {
    const todos = [...this.state.todos];
    todos.splice(index, 1);

    this.setState({
        todos: todos
    })
}

inputChangeHandler(event) {
    this.setState({
        inputValue: event.target.value
    });
}



toggleProBanner() {
  document.querySelector('.proBanner').classList.toggle("hide");
}

  render () {
    return (
      <div>
        <div className="row proBanner">
          <div className="col-12">
            <span className="d-flex align-items-center purchase-popup">
              <p>Like what you see? Check out our premium version for more.</p>
              <a href="https://github.com/BootstrapDash/StarAdmin-Free-React-Admin-Template" target="_blank" rel="noopener noreferrer" className="btn ml-auto download-button">Download Free Version</a>
              <a href="http://www.bootstrapdash.com/demo/star-admin-pro-react/template/demo_1/preview/dashboard" rel="noopener noreferrer" target="_blank" className="btn purchase-button">Upgrade To Pro</a>
              <i className="mdi mdi-close bannerClose" onClick={this.toggleProBanner}></i>
            </span>
          </div>
        </div>
        <div className="row">
          <div className="col-xl-3 col-lg-6 col-md-6 col-sm-6 grid-margin stretch-card">
            <div className="card card-statistics">
              <div className="card-body">
