import React, { Component } from 'react';
import { ProgressBar, Dropdown, Button,Modal } from 'react-bootstrap';
import axios from 'axios';
import "./shop.scss";

// import DatePicker from 'react-datepicker';
// import { Dropdown } from 'react-bootstrap';

export class ShopCategory extends Component {
  constructor(props) {
    super(props);
    this.state = {
      startDate : new Date(),
      persons : [],
      categories : [],
      show: false ,
      categoryName : '',
      categoryDescription :''

    }
    this.statusChangedHandler = this.statusChangedHandler.bind(this);
    this.addTodo = this.addTodo.bind(this);
    this.removeTodo = this.removeTodo.bind(this);
  } 
  componentDidMount() {
    this.fetchUsers();
    this.fetchCategories();
  }
  statusChangedHandler(event, id) {
    const todo = {...this.state.todos[id]};
    todo.isCompleted = event.target.checked;

    const todos = [...this.state.todos];
    todos[id] = todo;

    this.setState({
        todos: todos
    })
}

fetchUsers (){
  axios.get(`https://jsonplaceholder.typicode.com/users`)
      .then(res => {
        const personss = res.data;
        this.setState({ persons:personss });
      })
}
fetchCategories (){
    axios.get('http://localhost:5000/ShopCategories')
        .then(res => {
          const categories = res.data;
          this.setState({ categories:categories.categories });
        })
  }
fetchLocation (){
  var options = {
    enableHighAccuracy: true,
    timeout: 5000,
    maximumAge: 0
  };
  var e = '';
  navigator.geolocation.getCurrentPosition(function(pos){
    var crd = pos.coords;
    console.log(pos)
    var position = [crd.latitude,crd.longitude];
    console.log('position',position)
    axios.post('http://localhost:5000/updateLocation',{position:position})
    .then(res => {
      console.log(res.data);
    })
  }, function(e){
    console.log(e);

  }, options);
}

addTodo (event) {
    event.preventDefault();

    const todos = [...this.state.todos];
    todos.unshift({
        id: todos.length ? todos[todos.length - 1].id + 1 : 1,
        task: this.state.inputValue,
        isCompleted: false

    })

    this.setState({
        todos: todos,
        inputValue: ''
    })
}

removeTodo (index) {
    const todos = [...this.state.todos];
    todos.splice(index, 1);

    this.setState({
        todos: todos
    })
}

inputNameChangeHandler(event) {
    this.setState({categoryName : event.target.value});
}
inputDescriptionChangeHandler(event) {
  this.setState({categoryDescription : event.target.value});
}


toggleProBanner() {
  document.querySelector('.proBanner').classList.toggle("hide");
}
showModal = () => {
  this.setState({ show: true });
}

hideModal = () => {
  this.setState({ show: false });
}

  render () {
    return (
      <div>
        <div className="row  banner" >
          <div className="col-10">

              <p>Shop categories</p>

          </div>
          <div className="col-2" ><span className="d-flex justify-content-end" styles="{{align-self:'flex-end'}}"> <Button  variant="primary" onClick={this.showModal}>
                  Create shop <i className="fa fa-plus"></i>
              </Button></span></div>
        </div>

        <div className="row">
          <div className="col-lg-12 grid-margin">
            <div className="card">
              <div className="card-body">
                <h4 className="card-title">Orders</h4>
                <div className="table-responsive">
                  <table className="table table-striped">
                    <thead>
                      <tr>
                        <th> # </th>
                        <th> Shope name </th>
                        <th> Progress </th>
                        <th> Amount </th>
                        <th> Sales </th>
                        <th> Deadline </th>
                      </tr>
                    </thead>
                    <tbody>
                      {this.state.categories.map((person, index) =>{
                        return (
                      <tr>
                        <td className="font-weight-medium"> {index+1}</td>
                        <td> {person.name} </td>
                        <td>
                          <ProgressBar variant="success" striped now={25}/>
                        </td>
                        <td> $ 77.99 </td>
                        <td className="text-danger"> 53.64% <i className="mdi mdi-arrow-down"></i>
                        </td>
                        <td> May 15, 2015 </td>
                      </tr>)
                      })}

                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
        <Modal show={this.state.show}  onHide={this.hideModal}>
          <Modal.Header closeButton>
            <Modal.Title>Shop categories</Modal.Title>
          </Modal.Header>
          <Modal.Body>
          <form onSubmit={this.handleSubmit} noValidate>
            <div className="form-group">
              <label htmlFor="shopCategory">Name</label>
              <input type="email" className="form-control" id="shopCategory" placeholder="Category" value={this.state.categoryName} onChange={this.inputNameChangeHandler}></input>
            </div>
            <div className="form-group">
              <label htmlFor="shopCategoryDescription">Example textarea</label>
              <textarea className="form-control" id="shopCategoryDescription" rows="3" value={this.state.categoryDescription} onChange={this.inputDescriptionChangeHandler}></textarea>
            </div>
          </form>

          </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.hideModal}>
              Close
            </Button>
            <Button variant="primary">
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>
      </div> 
    );
  }
}


export default ShopCategory;
